mapper.update:
UPDATE OM_BUSIORG
id = #{id,jdbcType=VARCHAR}, busidomain = #{busidomain,jdbcType=VARCHAR}, busiorgid = #{busiorgid,jdbcType=INTEGER}, orgname = #{orgname,jdbcType=VARCHAR}, parentid = #{parentid,jdbcType=INTEGER}, orglevel = #{orglevel,jdbcType=INTEGER}, orgid = #{orgid,jdbcType=INTEGER}, nodetype = #{nodetype,jdbcType=VARCHAR}, orgcode = #{orgcode,jdbcType=VARCHAR}, seqno = #{seqno,jdbcType=VARCHAR}, manapos = #{manapos,jdbcType=INTEGER}, sortno = #{sortno,jdbcType=INTEGER}, isleaf = #{isleaf,jdbcType=VARCHAR}, subcount = #{subcount,jdbcType=INTEGER},
WHERE id = #{id}

mapper.select:
    	
    	select  from
   			(select a.*,rownum row_num from
      			(
      			select  from OM_BUSIORG 
        			${orderBy}
        			LIMIT ${offset}, ${length}
        		) a
   			) b 
		where b.row_num between ${offset} and ${length}
