mapper.update:
UPDATE OM_EMPLOYEE
id = #{id,jdbcType=VARCHAR}, empid = #{empid,jdbcType=INTEGER}, empcode = #{empcode,jdbcType=VARCHAR}, operatorid = #{operatorid,jdbcType=INTEGER}, userid = #{userid,jdbcType=VARCHAR}, empname = #{empname,jdbcType=VARCHAR}, realname = #{realname,jdbcType=VARCHAR}, gender = #{gender,jdbcType=VARCHAR}, birthdate = #{birthdate,jdbcType=TIMESTAMP}, position = #{position,jdbcType=INTEGER}, empstatus = #{empstatus,jdbcType=VARCHAR}, cardtype = #{cardtype,jdbcType=VARCHAR}, cardno = #{cardno,jdbcType=VARCHAR}, indate = #{indate,jdbcType=TIMESTAMP}, outdate = #{outdate,jdbcType=TIMESTAMP}, otel = #{otel,jdbcType=VARCHAR}, oaddress = #{oaddress,jdbcType=VARCHAR}, ozipcode = #{ozipcode,jdbcType=VARCHAR}, oemail = #{oemail,jdbcType=VARCHAR}, faxno = #{faxno,jdbcType=VARCHAR}, mobileno = #{mobileno,jdbcType=VARCHAR}, msn = #{msn,jdbcType=VARCHAR}, htel = #{htel,jdbcType=VARCHAR}, haddress = #{haddress,jdbcType=VARCHAR}, hzipcode = #{hzipcode,jdbcType=VARCHAR}, pemail = #{pemail,jdbcType=VARCHAR}, party = #{party,jdbcType=VARCHAR}, degree = #{degree,jdbcType=VARCHAR}, major = #{major,jdbcType=INTEGER}, specialty = #{specialty,jdbcType=VARCHAR}, workexp = #{workexp,jdbcType=VARCHAR}, regdate = #{regdate,jdbcType=TIMESTAMP}, createtime = #{createtime,jdbcType=TIMESTAMP}, lastmodytime = #{lastmodytime,jdbcType=TIMESTAMP}, orgidlist = #{orgidlist,jdbcType=VARCHAR}, orgid = #{orgid,jdbcType=INTEGER}, remark = #{remark,jdbcType=VARCHAR},
WHERE id = #{id}

mapper.select:

    	select  from
   			(select a.*,rownum row_num from
      			(
      			select  from OM_EMPLOYEE 
        			${orderBy}
        			LIMIT ${offset}, ${length}
        		) a
   			) b 
		where b.row_num between ${offset} and ${length}
