mapper.update:
UPDATE OM_POSITION
id = #{id,jdbcType=VARCHAR}, positionid = #{positionid,jdbcType=INTEGER}, posicode = #{posicode,jdbcType=VARCHAR}, posiname = #{posiname,jdbcType=VARCHAR}, posilevel = #{posilevel,jdbcType=INTEGER}, manaposi = #{manaposi,jdbcType=INTEGER}, dutyid = #{dutyid,jdbcType=INTEGER}, orgid = #{orgid,jdbcType=INTEGER}, positionseq = #{positionseq,jdbcType=VARCHAR}, positype = #{positype,jdbcType=VARCHAR}, createtime = #{createtime,jdbcType=TIMESTAMP}, lastupdate = #{lastupdate,jdbcType=TIMESTAMP}, updator = #{updator,jdbcType=INTEGER}, startdate = #{startdate,jdbcType=TIMESTAMP}, enddate = #{enddate,jdbcType=TIMESTAMP}, status = #{status,jdbcType=VARCHAR}, isleaf = #{isleaf,jdbcType=VARCHAR}, subcount = #{subcount,jdbcType=INTEGER},
WHERE id = #{id}

mapper.select:

    	select  from
   			(select a.*,rownum row_num from
      			(
      			select  from OM_POSITION 
        			${orderBy}
        			LIMIT ${offset}, ${length}
        		) a
   			) b 
		where b.row_num between ${offset} and ${length}
